var textList = []
texts.forEach(text => {
  text.wordCount = parseInt(text.wordCount)
  delete text._id
  textList.push({ title: text.title, wordCount: text.wordCount })
})
textList.sort((a, b) => (a.wordCount > b.wordCount) ? 1 : ((b.wordCount > a.wordCount) ? -1 : 0));
