const assert = require("assert")
const mongoClient = require("mongodb").MongoClient
const mongodbUrl = 'mongodb://127.0.0.1:27017/baykushizliokuma';
mongoClient.connect(mongodbUrl, { poolSize: 10, useNewUrlParser: true, useUnifiedTopology: true }, function (err, client) {
  assert.equal(null, err);
  db = client.db("baykushizliokuma");
  db.collection("texts").drop()
  db.collection("textList").drop()
  db.collection("texts").insertMany(texts, function (err, result) {
    console.log(result)
    db.collection("textList").insertMany(textList, function (err, result) {
      console.log(result)
      client.close()
    })
  })
  console.log("Connected successfully to database");
});